import React,{useState} from 'react'

const upercase=()=>{
    console.log(setText.upercase)
}
export default function TextForm(props) {
    const handleClick=()=>{
        console.log("Upercase was clicked");
        setText(upercase)
    }

    const handleonChange=()=>{
        console.log("handleonChange was clicked");
    }
    const [text, setText]=useState('enter text here');
    //text="hloo this is jai here"              worng way to change the text.
    // setText="enter text here again"          this is the right way to change the text.
    return (
        <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <label htmlFor="myBox" className="form-label">{props.TextArea}</label>
            <textarea className="form-control" value={text} onChange={handleonChange} id="myBox" rows="8"></textarea>
        </div>
        <button className='btn btn-primary' onClick={handleClick}>Convert into Upercase</button>
        </div>
  )
}
