import React,{useState} from 'react'

export default function TextForm(props) {
    // const handleClick=()=>{
    //     console.log("Upercase was clicked" + text);
    //     // let newText = text.toUpperCase();
    //     // setText(newText);   
    //     setText("you was clicked on handleClick" )
    // }

    const handleClick = () => {
        
        let newText = text.toUpperCase();
        setText(newText);
        
      }
    const handleclearClick = () => {
        
        let newText = " ";
        setText(newText);
        
      }
    const handleClick2 = () => {
        
        let newText = text.toLowerCase();
        setText(newText);
        
      }

      const handlesentenceClick = () => {
        
        let newText = text.charAt(0).toUpperCase()+ text.slice(1);
        setText(newText);
        
      }

    const handleonChange=(event)=>{
        
        setText(event.target.value);
    }
    const handlealternateClick=(event)=>{
      
      // let newText=Array.from(text).filter((char,index)=>index %2 ===0);
      // console.log(newText);
      let newText=text.split(" ").filter((char,index)=>index%2===0).toUpperCase().join(" ");
  }
    const handleCapitalizedClick=(event)=>{
      let newText=text.split(" ").map(word=>word.charAt(0).toUpperCase()+ word.slice(1)).join(" ");
      setText(newText);

    }
    const [text, setText]=useState('enter text here');
    //text="hloo this is jai here"              worng way to change the text.
    // setText="enter text here again"          this is the right way to change the text.
    return (
        <>
        <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <label htmlFor="myBox" className="form-label">{props.TextArea}</label>
            <textarea className="form-control" placeholder={text} value={text} onChange={handleonChange} id="myBox" rows="8"></textarea>
        </div>
        <button className='btn btn-primary' onClick={handleClick}>Convert into Upercase</button>
        <button className='btn btn-primary mx-3' onClick={handleClick2}>Convert into lowercase</button>
        <button className='btn btn-primary mx-3' onClick={handleclearClick}>clear text</button>
        <button className='btn btn-primary mx-3' onClick={handlesentenceClick}>sentence case</button>
        <button className='btn btn-primary mx-3' onClick={handleCapitalizedClick}>Capitalized case</button>
        <button className='btn btn-primary mx-3' onClick={handlealternateClick}>aLtErNaTiNg case</button>
        </div>
        <div className='container my-3'>
            <h2> Your Text summary</h2>
            <p> {text.split(" ").length} words and {text.split("").length}charectors are in the text your entered</p>
            <p>{0.008 * text.split(" ").length} minutes to read</p>
            <h2>Preview</h2>
            <p>{text}</p>
        </div>
        </>
  )
}
