import Navbar from "./component/Navbar";
// import About from "./component/About";
import TextForm from "./component/TextForm";
import React, {useState} from 'react';
import Alert  from "./component/Alert";



function App() {

  const [alert, setAlert]=useState(null)
  const showAlert=(type)=>{
    setAlert({
      type:type,

    })
  }
  const [mode,setMode]=useState('dark');// whether dark mode is enabled or not

  if(mode ==='dark'){
    document.body.style.backgroundColor="#0e3243";
    document.body.style.color='white';
    }else
    {
    document.body.style.backgroundColor='white';
    document.body.style.color='black';
  }

  const toggleMode=()=>{
    if(mode ==='dark'){
      setMode('light');
      document.body.style.backgroundColor="white";
      showAlert("light mode has been enabled",'success');

      }else
      {
      setMode('dark');
      document.body.style.backgroundColor='#0e3243';
      showAlert("dark mode has been enabled",'success');
    }
  }
  //{type: 'success', msg: `${mode} has been enabled`}
  return (
  <> 
  <Navbar title="TextUtil2" mode={mode} toggleMode={toggleMode}/>
  <Alert alert={alert}/>
  <div className="container my-3" >
  <TextForm showAlert={showAlert} heading="Enter the text to analyze" mode={mode} toggleMode={toggleMode} />
  </div>
  </>
  );
}

export default App;
