import Navbar from "./component/Navbar";
// import About from "./component/About";
import TextForm from "./component/TextForm";
import React, {useState} from 'react';
import Alert  from "./component/Alert";



function App() {
  const [alert, setAlert]=useState(null);

  const showAlert=(message, type)=>{      //alert ko ek object bnaya h
    setAlert({
      msg:message,
      type:type
    })
  }

  const [mode,setMode]=useState('dark');// whether dark mode is enabled or not
  if(mode ==='dark'){
    document.body.style.backgroundColor="#0e3243";
    document.body.style.color='white';
    setAlert("dark mode has been enabeld","success");

    }else
    {
    document.body.style.backgroundColor='white';
    document.body.style.color='black';
    setAlert("light mode has been enabeld","success");
  }
  const toggleMode=()=>{
    if(mode ==='dark'){
      setMode('light');
      document.body.style.backgroundColor="white";

      }else
      {
      setMode('dark');
      document.body.style.backgroundColor='#0e3243';

    }

  }
  return (
  <> 
  <Navbar title="TextUtil2" mode={mode} toggleMode={toggleMode}/>
  <Alert alert={alert}/>
  <div className="container my-3" >
  <TextForm heading="Enter the text to analyze" mode={mode} toggleMode={toggleMode}/>
  </div>
  </>
  );
}

export default App;
